iv>		<! Question 17>	<div class="questionLineOdd"><b>17. </b>&nbsp;&nbsp;趣味&nbsp;&nbsp;	<INPUT name="answer" type="text" onChange="checkAnswer('しゅみ',this.value,16)" style="width: 150px;">	</INPUT>hobby	<IMG SRC="../../../../grammar/gimages/blank.gif" class="symbols">	<p class="translationLine"></p></div>	<! Question 18>	<div class="questionLineEven"><b>18. </b>&nbsp;&nbsp;隣&nbsp;&nbsp;	<INPUT name="answer" type="text" onChange="checkAnswer('となり',this.value,17)" style="width: 150px;">    </INPUT>next door/to	<IMG SRC="../../../../grammar/gimages/blank.gif" class="symbols">	<p class="translationLine"></p></div>		<! Question 19>	<div class="questionLineOdd"><b>19. </b>&nbsp;&nbsp;池田&nbsp;&nbsp;	<INPUT name="answer" type="text" onChange="checkAnswer('いけだ',this.value,18)" style="width: 150px;">	</INPUT>	Ikeda (family name)	<IMG SRC="../../../../grammar/gimages/blank.gif" class="symbols">	<p class="translationLine"></p></div>	<! Question 20>	<div class="questionLineEven"><b>20. </b>&nbsp;&nbsp;番号&nbsp;&nbsp;	<INPUT name="answer" type="text" onChange="checkAnswer('ばんごう',this.value,19)" style="width: 150px;">    </INPUT>number	<IMG SRC="../../../../grammar/gimages/blank.gif" class="symbols">	<p class="translationLine"></p></div>		<! Question 21>	<div class="questionLineOdd"><b>21. </b>&nbsp;&nbsp;宿題&nbsp;&nbsp;	<INPUT name="answer" type="text" onChange="checkAnswer('しゅくだい',this.value,20)" style="width: 150px;">	</INPUT>homework	<IMG SRC="../../../../grammar/gimages/blank.gif" class="symbols">	<p class="translationLine"></p></div>	<! Question 22>	<div class="questionLineOdd"><b>22. </b>&nbsp;&nbsp;卒業&nbsp;&nbsp;	<INPUT name="answer" type="text" onChange="checkAnswer('そつぎょう',this.value,21)" style="width: 150px;">	</INPUT>graduation	<IMG SRC="../../../../grammar/gimages/blank.gif" class="symbols">	<p class="translationLine"></p></div>		<! Question 23>	<div class="questionLineEven"><b>23. </b>&nbsp;&nbsp;紹介&nbsp;&nbsp;	<INPUT name=