v>	<! Question 4>	<div class="questionLineEven"><b>4. </b>&nbsp;&nbsp;寄る&nbsp;&nbsp;	<INPUT name="answer" type="text" onChange="checkAnswer('よる',this.value,3)" style="width: 150px;">    </INPUT>to stop by	<IMG SRC="../../../../grammar/gimages/blank.gif" class="symbols">	<p class="translationLine"></p></div>		<! Question 5>	<div class="questionLineOdd"><b>5. </b>&nbsp;&nbsp;貸す&nbsp;&nbsp;	<INPUT name="answer" type="text" onChange="checkAnswer('かす',this.value,4)" style="width: 150px;">	</INPUT>to lend	<IMG SRC="../../../../grammar/gimages/blank.gif" class="symbols">	<p class="translationLine"></p></div>	<! Question 6>	<div class="questionLineEven"><b>6. </b>&nbsp;&nbsp;研究室&nbsp;&nbsp;	<INPUT name="answer" type="text" onChange="checkAnswer('けんきゅうしつ',this.value,5)" style="width: 150px;">    </INPUT>professor’s office	<IMG SRC="../../../../grammar/gimages/blank.gif" class="symbols">	<p class="translationLine"></p></div>		<! Question 7>	<div class="questionLineOdd"><b>7. </b>&nbsp;&nbsp;封筒&nbsp;&nbsp;	<INPUT name="answer" type="text" onChange="checkAnswer('ふうとう',this.value,6)" style="width: 150px;">	</INPUT>	envelope	<IMG SRC="../../../../grammar/gimages/blank.gif" class="symbols">	<p class="translationLine"></p></div>	<! Question 8>	<div class="questionLineEven"><b>8. </b>&nbsp;&nbsp;閉切&nbsp;&nbsp;	<INPUT name="answer" type="text" onChange="checkAnswer('しめきり',this.value,7)" style="width: 150px;">    </INPUT>deadline	<IMG SRC="../../../../grammar/gimages/blank.gif" class="symbols">	<p class="translationLine"></p></div>		<! Question 9>	<div class="questionLineOdd"><b>9. </b>&nbsp;&nbsp;期末試験&nbsp;&nbsp;	<INPUT name="answer" type="text" onChange="checkAnswer('きまつしけん',this.value,8)" style="width: 150px;">	</INPUT>final exam	<IMG SRC="../../../../grammar/gimages/blank.gif" class="symbols">	<p class="translationLine"></p></div>	<! Question 10>	<div class="questionLineEven"><b>10. </b>&nbsp;&nbsp;宛先&nbsp;&nbsp;	<INPUT name="answer" type="text" onChange="checkAnswer('あてさき',this.value,9)" style="width: 150px;">    </INPUT>address to send mail to	<IMG SRC="../../../../grammar/gimages/blank.gif" class="symbols">	<p class="translationLine"></p></div>		<! Question 11>	<div class="questionLineOdd"><b>11. </b>&nbsp;&nbsp;従業&nbsp;&nbsp;	<INPUT name="answer" type="text" onChange="checkAnswer('じゅぎょう',this.value,10)" style="width: 150px;">	</INPUT>class	<IMG SRC="../../../../grammar/gimages/blank.gif" class="symbols">	<p class="translationLine"></p></div>	<! Question 12>	<div class="questionLineEven"><b>12. </b>&nbsp;&nbsp;熱&nbsp;&nbsp;	<INPUT name="answer" type="text" onChange="checkAnswer('ねつ',this.value,11)" style="width: 150px;">    </INPUT>fever	<IMG SRC="../../../../grammar/gimages/blank.gif" class="symbols">	<p class="translationLine"></p></div>			</FORM><div id="score"></div><div><table class="optionBox"><tr>	<td><BUTTON onClick="checkAnswers()">Check Answers</BUTTON></td>	<td><BUTTON onClick="resetValues()">Reset Values</BUTTON></td></tr></table></div></div></body></html>