);questionArray[10] = new questionRecord("本田：社長、これを＿＿＿＿か。（タイプしましょう） ", "タイプいたしましょう", 4, ["タイプいたしましょう", "", "", ""]);questionArray[11] = new questionRecord("とよた：そうね、じゃ＿＿＿＿おいて。（タイプして） ", "タイプして", 4, ["タイプして", "", "", ""]);questionArray[12] = new questionRecord("上田：社長、明日ダラスに＿＿＿＿。（行きます）", "まいります", 4, ["まいります", "", "", ""]);showQuiz("FillInNoBank");</SCRIPT><CENTER><A HREF = "#Top">Return to Top</A></CENTER><p align=center><a href="../../../../gquiz.php">Return to Practice Quizzes  list page</p><!-- END CONTENT -->    </tr></table></BODY></HTML>